"""

# the output should be [6, 12, 17, 5, 12, 33, 1, 14, 13]

"""

unique_content_data = []
seen = set()

for sublist in content_data:
  item = tuple(sublist)
  if item not in seen:
    seen.add(item)
    unique_content_data.append(sublist)

print(f"song_list\n {songs_list}\n\nCONTENT_DATA\n{unique_content_data}\n\n")


final_result = []
seen_sublist = set()

for sublist in unique_content_data:
  not_in_songs_list = [item for item in sublist if item not in songs_list]
  in_songs_list = [item for item in sublist if item in songs_list]
  
  if tuple(not_in_songs_list) and tuple(not_in_songs_list) not in seen_sublist:
    final_result.append(not_in_songs_list)
    seen_sublist.add(tuple(not_in_songs_list))
  if tuple(in_songs_list) and tuple(in_songs_list) not in seen_sublist:
    final_result.append(in_songs_list)
    seen_sublist.add(tuple(in_songs_list))

print(f"song_list\n {songs_list}\n\nCONTENT_DATA\n{final_result}\n\n")

indices1 = []

for item in favorite_list:
  for sublist in final_result:
    if item in sublist:
      indices1.append(sublist.index(item) + 1)
      break

print(f"\nIndices {indices1}")
